import React, {Component} from 'react'
import {Button , Container, Row, Col, Form, InputGroup} from 'react-bootstrap'
import { BrowserRouter as Router, Route, Routes, useParams} from 'react-router-dom'

import 'bootstrap/dist/css/bootstrap.min.css'

import styles from './makeTransaction.css';

function withParams(Component) {
    return props => <Component {...props} params={useParams()} />;
  }

class MakeTransaction extends Component {  
    constructor(){

        super();
        this.state = {
            user:[],
            account: [],

        }
    }

    componentDidMount(){

        let { id } = this.props.params;


        const urls = [
            'http://localhost:3000/users/logged/'+id,
            'http://localhost:3000/users/account/'+id

        ]
        let requests = urls.map((url) => fetch(url));

        Promise.all(requests)
            .then((responses) => {
                return Promise.all(responses.map((response) => response.json()));
            })
            .then((responses) => {
                this.setState({
                    user : responses[0],
                    account : responses[1],

                });
            });

    }



    render (){


        return (
                <Container fluid style={{ fontcolor: 'black' }}>
                    <Row >
                        <Col className="mh-100" md lg="12">
                            <Row>
                                {this.state.user.map(user =>
                                    <h2> New Transaction for User #: {user.id}</h2>
                                )}
                            </Row>
                            <Row>
                                <Col className="mh-100" md lg="6">
                                    <Row>
                                        <h5>Transaction Amount:</h5>
                                        <InputGroup className="mb-3">
                                            <InputGroup.Text>$</InputGroup.Text>
                                            <Form.Control aria-label="Amount (to the nearest dollar)" 
                                                onChange={e => this.setState({ transactAmount: e.target.value })}
                                            />
                                            <InputGroup.Text>.00</InputGroup.Text>
                                        </InputGroup>
                                    </Row>
                                    <Row>
                                        <h5>From Account #:</h5>
                                        <Form.Select aria-label="Select Type"
                                            onChange={e => this.setState({ fromAccount: e.target.value })}
                                        >
                                            {this.state.account.map(account =>
                                                <option value={account.account_number}>{account.account_number}</option>

                                            )}
                                        </Form.Select>
                                    </Row>
                                    <Row>
                                        <h1> </h1>
                                    </Row>
                                    <Row>
                                        <h5>To Account #:</h5>
                                        <Form.Select aria-label="Select Type">
                                            {this.state.account.map(account =>
                                                <option value={account.account_number}>{account.account_number}</option>

                                            )}
                                        </Form.Select>
                                    </Row>
                                    <Row>
                                        <h5> </h5>
                                    </Row>

                                </Col>
                            </Row>
                            <Button variant="dark"
                            onClick={() => console.log(this.state.transactAmount, this.state.fromAccount)}
                            >
                                Submit
                            </Button>
                        </Col>
                    </Row>
                </Container>
        );
    }  
}

export default withParams(MakeTransaction);
